import React, { useState } from "react";
import "./otpverify.css";
import { Link } from "react-router-dom";
import axios from "axios";

export default function OTPVerifyPage() {
  const [OTP, setOtp] = useState("");
  const [email, setEmail] = useState("");
  const handleChange = (e) => {
    setOtp(1347);
  };

  const otpverify = async () => {
    try {
      const response = await axios({
        method: "post",
        url: "http://localhost:8080/otp-verify",
        data: {
          otp: OTP,
          email: email,
        },
      });
    } catch (error) {
      console.log("otp is not verify", error);
    }
  };

  return (
    <div className="otp">
      <h3 className="otpheading"> OTP Verify</h3>

      <input type="text" name="otp" maxLength={4} onBlur={handleChange}></input>
      <input
        type="password"
        name="password"
        placeholder="Enter your Password"
        onBlur={handleChange}
      ></input>

      <input
        type="text"
        name="email"
        placeholder="Enter your Email"
        onBlur={handleChange}
      ></input>

      <div className="row">
        <button className="button1" onClick={otpverify}>
          OTP verify
        </button>

        <Link to="/email-send" className="link">
          <button className="button2">Back</button>
        </Link>
      </div>
    </div>
  );
}
