import React, { useEffect, useRef, useState } from "react";
import "./resetpassword.css";
import { Link } from "react-router-dom";
import axios from "axios";

export default function ResetPasswordPage() {
  const emailRef = useRef();
  const [otpForm, showForm] = useState(true);
  const sendOtp = async (e) => {
    e.preventdefault();
    try {
      let url = "http://localhost:8080/email-send";
      let options = {
        method: "POST",
        url: url,
        data: { email: emailRef.current.value },
      };
      let response = await axios(options);
      let record = response.data;
      if ((record.statusText = "success")) {
        console.log(record.message);
        window.location.href = "/otp-verify";
        // showForm(false);
      } else {
        console.error(record.message);
      }
    } catch (e) {
      console.error("something went wrong", e);
    }
  };

  // const [email, setEmail] = useState("");
  // const handleChange = (e) => {
  //   setEmail(e.target.value);
  // };

  // const resetpassword = async () => {
  //   try {
  //     const response = await axios({
  //       method: "post",
  //       url: "http://localhost:8080/email-send",
  //       data: {
  //         email: email,
  //       },
  //     });
  //     window.location = "/otp-verify/" + email;
  //   } catch (error) {
  //     console.log("error while calling gettinr user", error);
  //   }
  // };
  return (
    <div className="reset">
      <h3 className="resetheading"> Reset Password</h3>
      <label className="labelheading">
        <b>Email:</b>
      </label>
      {otpForm ? (
        <form>
          <input
            type="text"
            name="email"
            placeholder="Enter your Email"
            ref={emailRef}
          ></input>

          <div className="row">
            <button className="button1" onClick={sendOtp}>
              Send otp
            </button>

            <Link to="/login" className="link">
              <button className="button2">Back</button>
            </Link>
          </div>
        </form>
      ) : (
        <p> hello </p>
      )}
    </div>
  );
}
