import React, { useState } from "react";
import "./login.css";
import axios from "axios";
import { useHistory, Link } from "react-router-dom";
import { InputAdornment } from "@mui/material";
import { MarkEmailRead } from "@mui/icons-material";
// import { InputAdornment, IconButton, SearchIcon } from "@mui/icons-material";

const Login = ({ setLoginUser }) => {
  const history = useHistory();
  const handleClick = () => {
    history.push("./register");
  };

  // const initialValues = { email: "", password: "" };
  // const [formValues, setFormValues] = useState(initialValues);

  const [user, setUser] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user,
      [name]: value,
    });
    setFormValues({ ...formValues, [name]: value });
    console.log(formValues);
  };

  const login = () => {
    console.log("Login");
    axios.post("http://localhost:8080/login", user).then((res) => {
      console.log(res.data);
      if (res.data.message === "login success") {
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("role", res.data.role);
        console.log(
          localStorage.getItem("token"),
          localStorage.getItem("role")
        );
        if (res.data.role === "Teacher") {
          window.location = "/Navbar";
        } else {
          window.location = "/homepage";
        }
        // alert(res.data.message);
        // window.location = "/homepage";
      }

      // setLoginUser(res.data.user);
      history.push("/login");
    });
  };

  const initialValues = { email: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  });

  const validate = (values) => {
    const errors = {};
    const validPassword = new RegExp("^(?=.*?[A-Za-z])(?=.*?[0-9]).{6,}$");
    if (!values.otp) {
      errors.otp = "otp is required";
    } else if (values.otp.length < 4) {
      errors.otp = "otp should be 4 characters";
    }

    if (!values.password) {
      errors.password = "password is required!";
    } else if (values.password.length < 4) {
      errors.password = "password must be more than 4 characters";
    } else if (values.password.length > 10) {
      errors.password = "password cannot more than 10 characters";
    } else if (!validPassword.test(values.password)) {
      errors.password = "This is not a valid password format!";
    }
    return errors;
  };

  return (
    <div className="loginbox">
      <div className="login">
        <h1 className="loginheading"> Login</h1>
        <input
          type="text"
          name="email"
          value={user.email}
          placeholder="Enter your Email"
          onChange={handleChange}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <MarkEmailRead
                  sx={{ color: "action.active", mr: 1, my: 0.5 }}
                />
              </InputAdornment>
            ),
          }}
        ></input>

        <input
          type="password"
          name="password"
          value={user.password}
          placeholder="Enter your Password"
          onChange={handleChange}
        ></input>
        <Link to="/email-send" style={{ textDecoration: "none" }}>
          <p className="forgot-password">
            <b>Forgot Password ?</b>
          </p>
        </Link>

        <div className="button" onClick={login}>
          Login
        </div>

        <div style={{ color: "black" }}>or</div>
        <div className="button" onClick={handleClick}>
          Register
        </div>
      </div>
    </div>
  );
};
export default Login;
