import React, { useState } from "react";
import "./otpverify.css";
import { Link } from "react-router-dom";
import axios from "axios";

export default function OTPVerifyPage() {
  const items = { ...localStorage };
  const email = items.email_reset;
  console.log(email);
  const [inputField, setInputField] = useState({
    otp: "",
    password: "",
    cpassword: "",
  });

  const inputHandler = (e) => {
    setInputField({ ...inputField, [e.target.name]: e.target.value });
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
    console.log(formValues);
  };

  const SubmitButton = async () => {
    let url = "http://localhost:8080/change-password";
    let options = {
      method: "POST",
      url: url,
      headers: {},
      data: { email, inputField },
    };
    try {
      let response = await axios(options);
      console.log(response);
      if (response.data.statusText === "success") {
        console.log("login successfully");
        localStorage.setItem("token", response.data.token);
        window.location.href = "/login";
      } else {
        console.log(response.data.message);
      }
    } catch (e) {
      console.error("something went wrong !");
    }
  };

  const initialValues = { otp: "", password: "", cpassword: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  });

  const validate = (values) => {
    const errors = {};
    const validPassword = new RegExp("^(?=.*?[A-Za-z])(?=.*?[0-9]).{6,}$");
    if (!values.password) {
      errors.password = "password is required!";
    } else if (!validPassword.test(values.password)) {
      errors.password = "This is not a valid password format!";
    }
    return errors;
  };

  // const [OTP, setOtp] = useState("");
  // const [email, setEmail] = useState("");
  // const handleChange = (e) => {
  //   setOtp(1347);
  // };

  // const otpverify = async () => {
  //   try {
  //     const response = await axios({
  //       method: "post",
  //       url: "http://localhost:8080/change-password",
  //       data: {
  //         otp: OTP,
  //         email: email,
  //       },
  //     });
  //   } catch (error) {
  //     console.log("otp is not verify", error);
  //   }
  // };

  return (
    <div className="otp">
      <form action="" className="otp" onSubmit={handleSubmit}></form>
      <h3 className="otpheading"> OTP Verify</h3>

      <input
        type="text"
        name="otp"
        placeholder="Enter your OTP"
        maxLength={4}
        onChange={inputHandler}
        value={formValues.otp}
      ></input>
      <input
        type="password"
        name="password"
        placeholder="Enter your Password"
        value={formValues.password}
        onChange={inputHandler}
      ></input>

      <input
        type="password"
        name="cpassword"
        placeholder="Enter your confirm Password"
        value={formValues.cpassword}
        onChange={inputHandler}
      ></input>
      <input
        type="hidden"
        name="email"
        value={email}
        onChange={inputHandler}
      ></input>

      <div className="row">
        <button className="button1" onClick={SubmitButton}>
          OTP verify
        </button>

        <Link to="/email-send" className="link">
          <button className="button2">Back</button>
        </Link>
      </div>
    </div>
  );
}
