import React, { useRef, useState, useEffect } from "react";
import "./resetpassword.css";
import { Link } from "react-router-dom";
import axios from "axios";

export default function ResetPasswordPage() {
  const emailRef = useRef();
  // const [otpForm, showForm] = useState(true);
  const sendOtp = async () => {
    // e.preventdefault();
    try {
      let url = "http://localhost:8080/email-send";
      let options = {
        method: "POST",
        url: url,
        data: { email: emailRef.current.value },
      };
      let response = await axios(options);
      let record = response.data;
      if ((record.statusText = "success")) {
        console.log(record.message);
        localStorage.setItem("email_reset", emailRef.current.value);
        window.location.href = "/otp-verify";
        // showForm(false);
      } else {
        console.error(record.message);
      }
    } catch (e) {
      console.error("something went wrong", e);
    }
  };

  const initialValues = { email: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) => {
    // console.log(e.target);
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
    console.log(formValues);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  });

  const validate = (values) => {
    const errors = {};
    const validEmail = new RegExp(
      "^[a-zA-Z0-9._:$!%-]+@[a-zA-Z0-9.-]+.[a-zA-Z]$"
    );
    if (!values.email) {
      errors.email = "email is required!";
    } else if (!validEmail.test(values.email)) {
      errors.email = "This is not a valid email format!";
    }
    return errors;
  };

  // const [email, setEmail] = useState("");
  // const handleChange = (e) => {
  //   setEmail(e.target.value);
  // };

  // const resetpassword = async () => {
  //   try {
  //     const response = await axios({
  //       method: "post",
  //       url: "http://localhost:8080/email-send",
  //       data: {
  //         email: email,
  //       },
  //     });
  //     window.location = "/otp-verify/" + email;
  //   } catch (error) {
  //     console.log("error while calling gettinr user", error);
  //   }
  // };
  return (
    <div>
      {/* <pre>{JSON.stringify(formValues, undefined, 2)}</pre> */}
      <form action="" className="reset" onSubmit={handleSubmit}>
        <h3 className="resetheading"> Reset Password</h3>
        <label className="labelheading">
          <b>Email:</b>
        </label>
        {/* {otpForm ? (
        <form> */}
        <input
          type="text"
          name="email"
          placeholder="Enter your Email"
          value={formValues.email}
          onChange={handleChange}
          ref={emailRef}
        ></input>
        <p className="errormessage">{formErrors.email}</p>

        <div className="row">
          <button className="button1" onClick={sendOtp}>
            Send otp
          </button>

          <Link to="/login" className="link">
            <button className="button2">Back</button>
          </Link>
        </div>
        {/* </form>
      ) : (
        <p> hello </p>
      )} */}
      </form>
    </div>
  );
}
