import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

import {
  FormGroup,
  FormControl,
  InputLabel,
  Input,
  Typography,
  styled,
  Button,
} from "@mui/material";

const Container = styled(FormGroup)`
  ${"" /* border: 2px solid red; */}
  width: 50%;
  margin: 3% auto 0 auto;
  & > div {
    margin-top: 20px;
  }
`;

const initialValue = {
  name: "",
  username: "",
  email: "",
  phone: "",
};

const EditUser = () => {
  const [user, setUser] = useState(initialValue);

  const getUser = async (id) => {
    try {
      const url = "http://localhost:8080/" + id;
      //   return await axios.get(url);
      fetch(url)
        .then((response) => response.json())
        .then((response) => {
          console.log("here", JSON.stringify(response.user).json());
          setUser(response.user);
          //   return data;
        });
    } catch (error) {
      console.log("error while calling gettinr user", error);
    }
  };

  const { id } = useParams();
  const loadUserDetails = async () => {
    const response = await getUser(id);
    setUser(response.data);
    console.log(response);
  };
  useEffect(() => {
    loadUserDetails();
  }, []);

  const onValueChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  // const addUserDetails = async () => {
  //   await addUser(user);
  // };

  const addstudent = () => {
    const { name, username, email, phone } = user;
    if (name && username && email && phone) {
      axios
        .post("http://localhost:8080/addstudent", user)
        .then((res) => console.log(res));
    } else {
      alert("invalid input");
    }
  };

  return (
    <Container>
      <Typography variant="h4">Edit User</Typography>
      <FormControl>
        <InputLabel>Name</InputLabel>
        <Input
          onChange={(e) => onValueChange(e)}
          name="name"
          value={user.name}
        />
      </FormControl>
      <FormControl>
        <InputLabel>Username</InputLabel>
        <Input
          onChange={(e) => onValueChange(e)}
          name="username"
          value={user.username}
        />
      </FormControl>
      <FormControl>
        <InputLabel>Email</InputLabel>
        <Input
          onChange={(e) => onValueChange(e)}
          name="email"
          value={user.email}
        />
      </FormControl>
      <FormControl>
        <InputLabel>Phone</InputLabel>
        <Input
          onChange={(e) => onValueChange(e)}
          name="phone"
          value={user.phone}
        />
      </FormControl>
      <FormControl>
        <Button variant="contained" onClick={addstudent}>
          Edit User
        </Button>
      </FormControl>
    </Container>
  );
};

export default EditUser;
