import React, { useState } from "react";
import "./otpverify.css";
import { Link } from "react-router-dom";
import axios from "axios";

export default function OTPVerifyPage() {
  const [inputField, setInputField] = useState({
    otpcode: "",
    password: "",
    cpassword: "",
  });
  const items = { ...localStorage };

  const inputHandler = (e) => {
    setInputField(
      { ...inputField, [e.target.name]: e.target.value },
      { email: items.email }
    );
  };
  const SubmitButton = async () => {
    let url = "http://localhost:8080/change-password";
    let options = {
      method: "POST",
      url: url,
      headers: {},
      data: { email: items.email, inputField },
    };
    try {
      let response = await axios(options);
      console.log(response);
      if (response.data.statusText === "success") {
        console.log("login successfully");
        localStorage.setItem("token", response.data.token);
      } else {
        console.log(response.data.message);
      }
    } catch (e) {
      console.error("something went wrong !");
    }
  };

  // const [OTP, setOtp] = useState("");
  // const [email, setEmail] = useState("");
  // const handleChange = (e) => {
  //   setOtp(1347);
  // };

  // const otpverify = async () => {
  //   try {
  //     const response = await axios({
  //       method: "post",
  //       url: "http://localhost:8080/change-password",
  //       data: {
  //         otp: OTP,
  //         email: email,
  //       },
  //     });
  //   } catch (error) {
  //     console.log("otp is not verify", error);
  //   }
  // };

  return (
    <div className="otp">
      <h3 className="otpheading"> OTP Verify</h3>

      <input
        type="text"
        name="otp"
        placeholder="Enter your OTP"
        maxLength={4}
        onChange={inputHandler}
      ></input>
      <input
        type="password"
        name="password"
        placeholder="Enter your Password"
        onChange={inputHandler}
      ></input>

      <input
        type="password"
        name="password"
        placeholder="Enter your confirm Password"
        onChange={inputHandler}
      ></input>

      <div className="row">
        <button className="button1" onClick={SubmitButton}>
          OTP verify
        </button>

        <Link to="/email-send" className="link">
          <button className="button2">Back</button>
        </Link>
      </div>
    </div>
  );
}
