import React from "react";
import "./resetpassword.css";
import { Link } from "react-router-dom";
import axios from "axios";

export default function ResetPasswordPage() {
  const resetpassword = () => {
    console.log("Login");
    axios.post("http://localhost:8080/login", user).then((res) => {
      console.log(res.data);
      if (res.data.message === "login success") {
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("role", res.data.role);
        console.log(
          localStorage.getItem("token"),
          localStorage.getItem("role")
        );
        if (res.data.role === "Teacher") {
          window.location = "/Navbar";
        } else {
          window.location = "/homepage";
        }
        // alert(res.data.message);
        // window.location = "/homepage";
      }
    });
  };
  return (
    <div className="reset">
      <h3 className="resetheading"> Reset Password</h3>
      <label className="labelheading">
        <b>Email:</b>
      </label>
      <input type="text" name="email" placeholder="Enter your Email"></input>

      <div className="row">
        <button className="button1" onClick={resetpassword}>
          Send otp
        </button>
        <Link to="/login" className="link">
          <button className="button2">Back</button>
        </Link>
      </div>
    </div>
  );
}
